<script>import useStyles from './Alert.styles';
import { Box } from '../Box';
import { CloseButton } from '../ActionIcon';
export let use = [], element = undefined, className = 'blue', override = {}, title = undefined, color = 'orange', radius = 'sm', variant = 'light', icon = undefined, iconSize = 16, iconProps = {}, withCloseButton = false, closeButtonLabel = undefined, onClose = () => { };
export { className as class };
$: ({ cx, classes, getStyles } = useStyles({ color, radius, variant }));
</script>

<Box
	{use}
	bind:element
	role="alert"
	class={cx(className, variant, getStyles({ css: override, variation: variant }))}
	{...$$restProps}
>
	<div class={classes.wrapper}>
		{#if icon}
			<slot class={classes.icon} name="icon">
				<svelte:component this={icon} class={classes.icon} {...iconProps} />
			</slot>
		{/if}

		<div class={classes.content}>
			{#if title}
				<div class={classes.title}>
					<span class={classes.label}>
						{title}
					</span>
					{#if withCloseButton}
						<CloseButton
							class={classes.closeButton}
							aria-label={closeButtonLabel}
							variant='transparent'
							size={iconSize}
							{iconSize}
							on:click={onClose}
						/>
					{/if}
				</div>
			{/if}
			<div class={classes.message}>
				<slot />
			</div>
		</div>
	</div>
</Box>
