<script>import { css as _css } from '../../styles';
import { createEventForwarder, useActions } from '../../internal';
import { get_current_component } from 'svelte/internal';
export let use = [], element = undefined, className = '', css = {}, root = undefined;
export { className as class };
/** An action that forwards inner dom node events from parent component */
const forwardEvents = createEventForwarder(get_current_component());
/** workaround for root type errors, this should be replaced by a better type system */
const castRoot = () => root;
let isHTMLElement;
let isComponent;
$: {
    isHTMLElement = root && typeof root === 'string';
    isComponent = root && typeof root === 'function';
}
$: BoxStyles = _css({});
</script>

<!--
@component

Add inline styles to any element or component with sx.
	
@see https://svelteui.org/core/box
@example
    ```svelte
    <Box css={{backgroundColor: '$blue600'}}>example</Box>
	<Box root='span'>example</Box>
	<Box css={{pt: '$20'}}>padding top of 5rem</Box> // uses the paddingTop shorthand, and theme scale
    ```
-->

{#if isHTMLElement}
	<!-- prettier-ignore -->
	<svelte:element
		bind:this={element}
		this={castRoot(root)}
		use:forwardEvents
		use:useActions={use}
		class="{className} {BoxStyles({ css })}"
		{...$$restProps}
	>
		<slot></slot>
	</svelte:element>
{:else if isComponent}
	<svelte:component
		this={root}
		bind:this={element}
		use={[forwardEvents, [useActions, use]]}
		class="{className} {BoxStyles({ css })}"
		{...$$restProps}
	>
		<slot />
	</svelte:component>
{:else}
	<div
		bind:this={element}
		use:forwardEvents
		use:useActions={use}
		class="{className} {BoxStyles({ css })}"
		{...$$restProps}
	>
		<slot />
	</div>
{/if}
