<script>import useStyles from './Notification.styles';
import { Box } from '../Box';
import { CloseButton } from '../ActionIcon';
import { Loader } from '../Loader';
import { Text } from '../Text';
export let use = [], element = undefined, className = 'blue', override = {}, title = undefined, color = 'blue', radius = 'sm', loading = false, icon = undefined, iconSize = 16, iconProps = {}, withCloseButton = true, closeButtonLabel = undefined, closeButtonProps = {}, onClose = () => { };
export { className as class };
$: ({ cx, classes } = useStyles({ color, radius }, { override }));
</script>

<Box
	{use}
	bind:element
	role="alert"
	class={cx(className, classes.root, {
		withIcon: icon,
		withLoader: loading
	})}
	{...$$restProps}
>
	{#if icon && !loading}
		<slot class={classes.icon} name="icon">
			<svelte:component this={icon} class={classes.icon} {...iconProps} />
		</slot>
	{/if}
	{#if loading}
		<Loader class={classes.loader} size={28} {color} />
	{/if}
	<div class={classes.content}>
		{#if title}
			<Text class={classes.title} size="sm" weight={500}>
				{title}
			</Text>
		{/if}
		<Text class={classes.description} size="sm" color="dimmed">
			<slot />
		</Text>
	</div>
	{#if withCloseButton}
		<CloseButton
			class={classes.closeButton}
			aria-label={closeButtonLabel}
			variant="hover"
			size={28}
			{iconSize}
			{...closeButtonProps}
			on:click={onClose}
		/>
	{/if}
</Box>
