<script>import useStyles from './Title.styles';
import { Text } from '../Text';
import { titleSizes } from './Title.styles';
export let use = [], element = undefined, className = '', override = {}, align = 'left', order = 1, color = 'dark', transform = 'none', variant = 'text', weight = 'normal', gradient = { from: 'indigo', to: 'cyan', deg: 45 }, inline = true, lineClamp = undefined, underline = undefined, inherit = false, href = '', tracking = 'normal';
export { className as class };
let node;
$: node = `h${order}`;
$: ({ cx, getStyles } = useStyles());
</script>

<!--
@component

Display text that uses title styling and title HTML tags.
	
@see https://svelteui.org/core/title
@example
    ```tsx
    <Title>This is a title</Title> // standard title component
    <Title order={3}>This is a title</Title> // title component with order 2
    <Title order={1} override={{ fontSize: '90px' }}>This is a bigger title</Title> // title component with override style
    ```
-->

<Text
	bind:element
	class={cx(className, { title: true }, getStyles({ css: override }))}
	root={node}
	size={titleSizes[order].fontSize}
	{use}
	{align}
	{color}
	{transform}
	{variant}
	{weight}
	{gradient}
	{inline}
	{lineClamp}
	{underline}
	{inherit}
	{href}
	{tracking}
	{...$$restProps}
>
	<slot />
</Text>
