<script context="module">import { setContext } from 'svelte';
import { beforeUpdate, get_current_component } from 'svelte/internal';
import { mergeTheme } from '../';
import { useSvelteUITheme } from './default-theme';
import { colorScheme } from './svelteui.stores';
import { key, ssrStyles, useSvelteUIThemeContext } from './svelteui.provider';
import { dark, css, getCssText, NormalizeCSS, SvelteUIGlobalCSS } from '../../index';
import { createEventForwarder, useActions } from '../../../internal';
</script>

<script>export let use = [], className = '', element = undefined, theme = useSvelteUITheme(), styles = {}, defaultProps = {}, themeObserver = 'light', withNormalizeCSS = false, withGlobalStyles = false, ssr = false, override = {}, inherit = false;
export { className as class };
beforeUpdate(() => {
    const htmlClassList = document.documentElement.classList;
    if ($colorScheme === 'dark')
        htmlClassList.add(dark.className);
    if ($colorScheme === 'light')
        htmlClassList.remove(dark.className);
});
const ctx = useSvelteUIThemeContext();
const forwardEvents = createEventForwarder(get_current_component());
const ProviderStyles = css({});
const DEFAULT_THEME = useSvelteUITheme();
$: if (withGlobalStyles)
    SvelteUIGlobalCSS();
$: if (withNormalizeCSS)
    NormalizeCSS();
$: overrides = {
    themeOverride: inherit ? { ...ctx.theme, ...theme } : theme,
    styles: inherit ? { ...ctx.styles, ...styles } : styles,
    defaultProps: inherit ? { ...ctx.styles, ...defaultProps } : defaultProps
};
$: setContext(key, {
    theme: overrides.themeOverride,
    styles: {},
    defaultProps: {}
});
$: colorScheme.set(themeObserver);
$: mergedTheme = mergeTheme(DEFAULT_THEME, overrides.themeOverride);
</script>

<svelte:head>
	{#if ssr}
		{@html ssrStyles(getCssText)}
	{/if}
</svelte:head>

<div
	id="SVELTEUI_PROVIDER"
	bind:this={element}
	use:useActions={use}
	use:forwardEvents
	class="{className} {ProviderStyles({ css: override })} {themeObserver === 'light'
		? mergedTheme
		: dark}"
	{...$$restProps}
>
	<slot />
</div>
