<script>export let animation = 'hue';
export let duration = 20;
export let easing = 'linear';
export let iterations = 'infinite';
export let optimalSettings = true;
export let gradient = { from: '#f35626', to: '#feab3a', deg: 92 };
$: duration = `${duration}s`;
$: if (optimalSettings) {
    if (animation === 'hue') {
        duration = 20;
        easing = 'linear';
        iterations = 'infinite';
    }
    else if (animation === 'flow') {
        duration = 4;
        easing = 'ease-in-out';
        iterations = 'infinite';
    }
}
</script>

<div
	class={`animation-${animation}`}
	style={`--duration: ${duration}s; --easing: ${easing}; --iterations: ${iterations}; --from: ${gradient.from}; --to: ${gradient.to}; --deg: ${gradient.deg}deg;`}
>
	<slot />
</div>

<style>.animation-hue{animation:hue var(--duration) var(--easing) var(--iterations);-webkit-animation:hue var(--duration) var(--easing) var(--iterations);background-clip:text;-webkit-background-clip:text;background-image:-webkit-linear-gradient(var(--deg),var(--from),var(--to));color:#228be6}.animation-flow,.animation-hue{-webkit-text-fill-color:transparent}.animation-flow{animation:flow var(--duration) var(--easing) var(--iterations);-webkit-animation:flow var(--duration) var(--easing) var(--iterations);background:linear-gradient(-75deg,#7f7fd5,#e786d7);background-clip:text;-webkit-background-clip:text;background-size:200%}@-webkit-keyframes hue{0%{-webkit-filter:hue-rotate(0deg)}to{-webkit-filter:hue-rotate(-1turn)}}@keyframes hue{0%{-webkit-filter:hue-rotate(0deg)}to{-webkit-filter:hue-rotate(-1turn)}}@-webkit-keyframes flow{0%{background-position:0 50%}50%{background-position:90% 75%}to{background-position:0 50%}}@keyframes flow{0%{background-position:0 50%}50%{background-position:90% 75%}to{background-position:0 50%}}</style>
